sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var rightnow = GlideDateTime();
    var employee = new GlideRecord('x_58872_needit_employee');
    employee.query();
    var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
    overdueNITasks.addQuery('due_date','<=',rightnow);
    overdueNITasks.addQuery('state','<',3);
    overdueNITasks.addQuery('assigned_to',current.assigned_to);
    overdueNITasks.query();
    template.print("In addition to this overdue NeedIt Task, you have " + (overdueNITasks.getRowCount() - 1) + " more overdue NeedIt Tasks.");

    template.print('<table border="1" style="border-collapse: collapse; border-style: solid; border-color: #b3a8a8;">'); 
	template.print('<tr>'); 
	template.print('<th style="padding: 5px;">Mã Nhân viên</th>'); 
	template.print('<th style="padding: 5px;">Tên Nhân viên</th>');
	template.print('<th style="padding: 5px;">Tuổi</th>');
	template.print('<th style="padding: 5px;">Số điện thoại</th>');
	template.print('<th style="padding: 5px;">Địa Chỉ</th>');
	template.print('<th style="padding: 5px;">Quê Quán</th>');
	template.print('<th style="padding: 5px;">Quốc Tịch</th>');
	template.print('</tr>'); 

    while(employee.next())
    {
        template.print('<tr>');
        template.print('<td>'+employee.getValue('number')+'</td>');
        template.print('<td>'+employee.getValue('name')+'</td>');
        template.print('<td>'+employee.getValue('age')+'</td>');
        template.print('<td>'+employee.getValue('sdt')+'</td>');
        template.print('<td>'+employee.getValue('diachi')+'</td>');
        template.print('<td>'+employee.getValue('quequan')+'</td>');
        template.print('<td>'+employee.getValue('quoctich')+'</td>');
        template.print('</tr>');
    }
    template.print('</table>');
})(current, template, email, email_action, event);
